import React, { Component } from 'react'
import {Form, FormGroup,Label,Input} from 'reactstrap'
import PropTypes from 'prop-types'

import './RentalForm.css';


export class RentalForm extends Component {
    static propTypes = {
    }
    constructor(props){
        super(props);
        this.state={
            n_apt: 0,
            s_street: "",
            size : 3,
            pet: false,
            smoke: false
        }
    }

    petAllowHandler = () =>{
        this.setState({pet: !this.state.pet})
    }


    smokeAllowHandler = () =>{
        this.setState({pet: !this.state.smoke})
    }


    size

    render() {

        return (
            <div className="retalFormbg">
                <h1>Apartment Register Form</h1>

                <Form className="retalForm">
                    <FormGroup>
                        <Label className="lable">Apartment number</Label>
                        <Input placeholder = "#ApportmentNumber" type="number" ></Input>
                        <Label className="lable">Street</Label>
                        <Input placeholder = "street" type="text"></Input>
                        <Label className="lable">Size</Label>
                        <Input type="select">
                        <option value="1">
                            studio
                        </option>
                        <option value="3">
                            3&frac12;
                        </option>
                        <option value="4">
                            4&frac12;
                        </option>
                        <option  value="5">
                            5&frac12;
                        </option>
                        <option  value="6">
                           bigger
                        </option>
                        </Input>
                        <Label className="lable">Rent</Label>
                        <Input placeholder = "$" type="number"></Input>
                        <Label className="lable">Checkbox</Label>
                        <br/>
                        <span className="checkbox">
                        <Input type="checkbox" checked={this.state.pet} onClick={this.petAllowHandler}></Input>pet allow
                        </span>         
                        <br/>
                        <span className="checkbox">
                        <Input type="checkbox" checked={this.state.smoke} onClick={this.petAllowHandler}></Input>smoke allow
                        </span>               
                    </FormGroup>
                </Form>
            </div>
        )
    }
}

export default RentalForm
