import React, { Component } from 'react'
import {Form, FormGroup,Label,Input,Button} from 'reactstrap'

import axios from 'axios';
import './RegisterForm.css';
export default class RegisterForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            username :"",
            email:"",
            password: "",
            re_password:"",
            req_name:"",
            req_email:"",
            req_password:"",
            req_re_password:""
        }
    }

    registerHandler=()=>{
            if(this.state.name==="")
            {
                this.setState({req_name: "input_required"})
            }
            else if(this.state.email===""){
                this.setState({req_email: "input_required"})       
            }
            else if(this.state.password===""||this.state.password!==this.state.re_password){
                this.setState({req_password: "input_required"})       
            }
            else if(this.state.re_password===""||this.state.password!==this.state.re_password){
                this.setState({req_re_password: "input_required"})       
            }
            else{
                console.log("happy")
            }



    }


    render() {   
        return (
            <div className="retalFormbg">
                <h1>Proprieor RegisterForm</h1>

                <Form className="retalForm">
                    <FormGroup>
                        <Label className={'lable'+this.state.req_name}>Username</Label>
                        <Input placeholder = "username" onChange={(e)=>{this.setState({name: e.target.value})}} value={this.state.name}></Input>
                        <Label className={"lable" + this.state.req_email}>E-mail</Label>
                        <Input placeholder = "e-mail" type="email" onChange={(e)=>{this.setState({email: e.target.value})}} value={this.state.email} ></Input>
                        <Label className={"lable"+ this.state.req_password}>password</Label>
                        <Input placeholder = "password" type="password" onChange={(e)=>{this.setState({password: e.target.value})}} value={this.state.password} ></Input>
                        <Label className={"lable" + this.state.req_re_password}>re-password</Label>
                        <Input placeholder = "re-password" type="password" onChange={(e)=>{this.setState({re_password: e.target.value})}} value={this.state.re_password}></Input>    
                        <br/>                   
                        <Button onClick={this.registerHandler}  >Register</Button>    
                    </FormGroup>
                </Form>
            </div>
        )

    }
}
