import React, { Component } from 'react'
import {connect} from 'react-redux';
import { func } from 'prop-types';

class P_Dasboard extends Component {
    constructor(props){
        super(props);
        this.state = {
            p_username :""
        }
    }

    logout = () =>{
        localStorage.removeItem("p_username");
        this.props.history.push('/');
    }

    componentDidMount(){
        this.autoLogout();
    }


    autoLogout =()=>{ alert("tee");
        setTimeout(()=>{
           if(window.confirm("tttt"))
           {
            alert("no");

           }
           else
           {
            alert("yes");
           }
        },3000)
    }



    render() {
        const v = localStorage.getItem("p_username");
        if(v ===null)
        {
            return(
                <div>
                    <h1>It's only access for house owner</h1>
                </div>
            )
        }
       else
       {
        return (
            <div>
                <h1>P_Dasboard</h1>

                <br/>
                <button onClick={this.logout}>Logout</button>
            </div>
        )
    }

    }
}




const mapStateToProps = (store) =>{
    return{
        p_username:store.proprietor.name
    }
}

export default connect(mapStateToProps)(P_Dasboard);
